    public void onSensorChanged(int sensor, float[] values) {
        if (sensor == 2) {
            long curTime = System.currentTimeMillis();
            if (curTime - this.lastUpdate > 100) {
                long diffTime = curTime - this.lastUpdate;
                this.lastUpdate = curTime;
                this.f0x = values[0];
                this.f1y = values[1];
                this.f2z = values[2];
                if ((Math.abs(((((this.f0x + this.f1y) + this.f2z) - this.last_x) - this.last_y) - this.last_z) / ((float) diffTime)) * 10000.0f > 800.0f) {
                    /* Call new function here.. */
                    Shaken();
                }
                this.last_x = this.f0x;
                this.last_y = this.f1y;
                this.last_z = this.f2z;
            }
        }
    }
